parameters.jsCode:
// Get the input HTML
const textHtml = $input.first().json.textAsHtml || "";

// Regex to detect LinkedIn Service Marketplace project URL
const serviceRequestRegex = /https:\/\/www\.linkedin\.com\/service-marketplace\/projects\/[^\s"']+/i;

// Check if it's a Service Request
const isServiceRequest =
  textHtml.includes("Submit proposal") &&
  serviceRequestRegex.test(textHtml);

// Extract proposal URL if it's a Service Request
const proposalUrl = isServiceRequest
  ? textHtml.match(serviceRequestRegex)?.[0] || null
  : null;

// Return in the desired format
return [
  {
    json: {
      classification: isServiceRequest ? "Service Request" : "Other",
      proposalUrl: proposalUrl,
      message_id: $input.first().json.id,
    }
  }
];

